<?php/*Template name: kontakt*/get_header();?><style type="text/css">.acf-map {	width: 100%;	height: 400px;	border: #ccc solid 1px;	margin: 20px 0;}/* fixes potential theme css conflict */.acf-map img {max-width: inherit !important;}</style><script src="https://maps.googleapis.com/maps/api/js?v=3.exp&sensor=false"></script><script type="text/javascript">(function($) {/**  new_map**  This function will render a Google Map onto the selected jQuery element*	*  @type	function	*  @date	8/11/2013	*  @since	4.3.0*	*  @param	$el (jQuery element)	*  @return	n/a*/function new_map( $el ) {		// var	var $markers = $el.find('.marker');			// vars	var args = {				zoom		: 16,				center		: new google.maps.LatLng(0, 0),			mapTypeId	: google.maps.MapTypeId.ROADMAP	};					// create map	var map = new google.maps.Map( $el[0], args);			// add a markers reference	map.markers = [];			// add markers	$markers.each(function(){			add_marker( $(this), map );			});			// center map	center_map( map );			// return	return map;	}/**  add_marker**  This function will add a marker to the selected Google Map*	*  @type	function	*  @date	8/11/2013	*  @since	4.3.0*	*  @param	$marker (jQuery element)	*  @param	map (Google Map object)	*  @return	n/a*/function add_marker( $marker, map ) {	// var	var latlng = new google.maps.LatLng( $marker.attr('data-lat'), $marker.attr('data-lng') );	// create marker	var marker = new google.maps.Marker({			position	: latlng,					map			: map	});	// add to array	map.markers.push( marker );	// if marker contains HTML, add it to an infoWindow	if( $marker.html() )	{		// create info window		var infowindow = new google.maps.InfoWindow({					content		: $marker.html()		});		// show info window when marker is clicked		google.maps.event.addListener(marker, 'click', function() {			infowindow.open( map, marker );		});	}}/**  center_map**  This function will center the map, showing all markers attached to this map*	*  @type	function	*  @date	8/11/2013	*  @since	4.3.0*	*  @param	map (Google Map object)	*  @return	n/a*/function center_map( map ) {	// vars	var bounds = new google.maps.LatLngBounds();	// loop through all markers and create bounds	$.each( map.markers, function( i, marker ){		var latlng = new google.maps.LatLng( marker.position.lat(), marker.position.lng() );		bounds.extend( latlng );	});	// only 1 marker?	if( map.markers.length == 1 )	{		// set center of map	map.setCenter( bounds.getCenter() );	map.setZoom( 16 );	}	else	{		// fit to bounds		map.fitBounds( bounds );	}}/**  document ready**  This function will render each map when the document is ready (page has loaded)*	*  @type	function	*  @date	8/11/2013	*  @since	5.0.0*	*  @param	n/a	*  @return	n/a*/// global varvar map = null;$(document).ready(function(){	$('.acf-map').each(function(){		// create map		map = new_map( $(this) );	});});})(jQuery);</script><section class="section-white single kontakt">	<div class="container">		<div class="row">			<h3 class="title">Välkommen att kontakta oss!</h3>		</div>		<div class="row">		<div class="map">			<div class="col-md-8 col-sm-4">			<div class="row">				<div class="col-md-12"><iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2003.6500244299789!2d17.65595881623747!3d59.85495217580862!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x465fcbfdfd48f185%3A0xb6c1699fb559b48f!2sTrepe+Bilv%C3%A5rd+AB+-+Autoexperten!5e0!3m2!1ssv!2sus!4v1464334661689" width="800" height="400" frameborder="0" style="border:0" allowfullscreen></iframe>				</div>			</div>				<div class="border-wrap">				<h4 class="small-title">Fyll i formuläret här nedan så återkommer vi så fort vi kan</h4>								<?php the_field('kontaktformular');?>				</div>								</div>			</div>			<div class="col-md-4 col-sm-4">				<div class="border-wrap">					<h4 class="small-title">Kontaktinformation</h4>					<ul class="kontakt-list">						<li class="clearfix"><label><i class="fa fa-phone"></i>Telefon: </label><label><?php the_field('telefon'); ?></label></li>						<li class="clearfix"><label><i class="fa fa-envelope"></i>E-post: </label><label><a href="mailto:<?php the_field('mail'); ?>"><?php the_field('mail'); ?></a></label></li>					</ul>					<ul class="kontakt-list">						<li class="clearfix"><label><i class="fa fa-home"></i>Adress: </label><label><?php the_field('adress');?></label></li>						<li class="clearfix"><label><i class="fa fa-envelope"></i>Postnr: </label><label><?php the_field('postnummer');?></label></li>						<li class="clearfix"><label><i class="fa fa-building"></i>Postort: </label><label><?php the_field('postort');?></label></li>					</ul>					<ul class="kontakt-list">						<li class="clearfix"><label><i class="fa fa-clock-o" aria-hidden="true"></i></label>						<ul>							<?php the_field('öppettider');?>						</ul>						</li>					</ul>									</div>			</div>		</div>	</div></section><?php get_footer(); ?>